function generateKey(){	  
       $chars = "0123456789abcdefghijklmnopqrstuvwxyzABCDEFGHIJKLMNOPQRSTUVWXYZ"; // Base62
       $key = '';		
       for ($i = 0; $i < 32; $i++)
       {
            $randNum = rand(0, 61);
            $key .= $chars[$randNum];
       }
       return $key; 	
}

