function descToStage($desc)
{

    switch ($desc) {
        case "Visits":
            return "0";
            break;
        case "Installs":
            return "2";
            break;
        case "Downloads":
            return "1";
            break;
        case "Success":
            return "10";
            break;
        case "Goal#1":
            return "21";
            break;
        case "Goal#2":
            return "22";
            break;
        default:
            return "999";
    }

}

